void main(List<String> args) async {
  final String realInput =
      "";

  print("Start-of-packet found at char : ${findStartOfPacket(realInput)}");
  print("Start-of-message found at char : ${findStartOfMessage(realInput)}");
}

int? findStartOfPacket(String dataStreamBuffer) {
  return _findMarkerWithXDistinctsChar(dataStreamBuffer, 4);
}

int? findStartOfMessage(String dataStreamBuffer) {
  return _findMarkerWithXDistinctsChar(dataStreamBuffer, 14);
}

int? _findMarkerWithXDistinctsChar(String dataStreamBuffer, int distinctsChar) {
  for (int i = distinctsChar; i < dataStreamBuffer.length; i++) {
    final Set<String> charSet =
        dataStreamBuffer.substring(i - distinctsChar, i).split('').toSet();
    // print('selected string : ${dataStreamBuffer.substring(i - 4, i)}');
    // print('selectedCharSet length : ${charSet.length}');

    if (charSet.length == distinctsChar) {
      return i;
    }
  }
  return null;
}
